import './Hero.css'
import { BrowserRouter as Router, Switch, Route, NavLink } from 'react-router-dom';

function Hero() {
     const herobg = {
         backgroundImage:`url(${window.location.origin+ "/img/hero-banner-bg.jpg"})`
     }
    return(
        <div className='heroslider' style={herobg}> 
           <div className='container'>
               <div className='row'>
                   <div className='col-md-6'>
                       <h1>Interact with your <span>Fans Sell Content</span> to your audience</h1>
                       <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit.</p>
                       <NavLink className='white-btn' to="/#">Start Today</NavLink>
                   </div>
                   <div className='col-md-6'>
                     <div className='hero-mobile'>
                         <img src='/src/img/mobile-frame.png' className='mobile-frame' alt='Mobile application' />
                     </div>
                   </div>
               </div>
            </div> 
        </div>
    );
}
export default Hero;
